(function(){tinymce.create("tinymce.plugins.AdvancedImagePlugin",{init:function(a,b){a.addCommand("sys_image",function(){if(a.dom.getAttrib(a.selection.getNode(),"class").indexOf("mceItem")!=-1){return}a.windowManager.open({file:b+"/image.htm",width:480+parseInt(a.getLang("sys_image.delta_width",0)),height:385+parseInt(a.getLang("sys_image.delta_height",0)),inline:1},{plugin_url:b})});a.addButton("image",{title:"Вставить картинку",cmd:"sys_image"})},getInfo:function(){return{longname:"Advanced image",author:"Moxiecode Systems AB",authorurl:"http://tinymce.moxiecode.com",infourl:"http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/sys_image",version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add("sys_image",tinymce.plugins.AdvancedImagePlugin)})();